mapper.select:
select userid, userpw, nickname, qualify 
from tbl_user 
where userid=#{userid} 
and userpw=#{userpw}

mapper.insert:
insert into tbl_user 
(userid, userpw, username, nickname, email, addr1, addr2, addr3, rdate) 
values (#{userid},#{userpw},#{username},#{nickname},#{email},#{addr1},#{addr2},#{addr3}, sysdate());
