mapper.select:
select userid, userpw, nickname, qualify, profileImg 
from tbl_user 
where userid=#{userid} 
and userpw=#{userpw}

mapper.insert:
insert into tbl_user 
(userid, userpw, username, nickname, email, addr1, addr2, addr3, rdate) 
values (#{userid},#{userpw},#{username},#{nickname},#{email},#{addr1},#{addr2},#{addr3}, sysdate());

mapper.select:
select * from (select @rownum:=@rownum+1 as rownum, userid, username, nickname, rdate, qualify, profileImg from (select @rownum:=0) as rownum , tbl_user order by rdate desc) tbl_user
where rownum >(#{pageNum}-1)*#{amount} and rownum<=#{pageNum}*#{amount}
;
